SELECT wo.id, wo.appointment_type, wo.association_id, wo.business, wo.create_time, wo.doctor_id, wo.is_confirm, wo.is_delete, wo.is_reservation, wo.medical_number, wo.ord_type, wo.order_number, wo.order_price, wo.order_type, wo.perioperative_surgery, wo.surgery_name, wo.surgery_time, wo.taking_lens, wo.task_id, wo.update_time, wo.wound_condition, wo.wound_photo_ids, wo.wound_photography_time, tp.`name`, tp.barcode_photo, tp.phone, doctor.doctor_name, doctor.doctor_introduce, doctor.doctor_photo, doctor.doctor_services, doctor.doctor_type, group_concat( trust_file.file_url ORDER BY trust_file.file_name DESC SEPARATOR '|' ) AS url, group_concat( trust_file.file_id ORDER BY trust_file.file_id DESC SEPARATOR '|' ) AS fileId, ( SELECT group_concat( trust_file.file_url ORDER BY trust_file.file_name DESC SEPARATOR '|' ) AS url FROM doctor LEFT JOIN trust_relation_file ON doctor.id = trust_relation_file.busi_id AND trust_relation_file.busi_type = 'doctor' LEFT JOIN trust_file ON trust_relation_file.file_id = trust_file.file_id ) doctorUrl, ( SELECT group_concat( trust_file.file_id ORDER BY trust_file.file_id DESC SEPARATOR '|' ) AS fileId FROM doctor LEFT JOIN trust_relation_file ON doctor.id = trust_relation_file.busi_id AND trust_relation_file.busi_type = 'doctor' LEFT JOIN trust_file ON trust_relation_file.file_id = trust_file.file_id ) doctorFileId, ( SELECT group_concat( trust_file.file_url ORDER BY trust_file.file_name DESC SEPARATOR '|' ) AS url FROM tracking_personnel LEFT JOIN trust_relation_file ON tracking_personnel.id = trust_relation_file.busi_id AND trust_relation_file.busi_type = 'trackingPersonne' LEFT JOIN trust_file ON trust_relation_file.file_id = trust_file.file_id ) tarkUrl, ( SELECT group_concat( trust_file.file_id ORDER BY trust_file.file_id DESC SEPARATOR '|' ) AS fileId FROM tracking_personnel LEFT JOIN trust_relation_file ON doctor.id = trust_relation_file.busi_id AND trust_relation_file.busi_type = 'trackingPersonne' LEFT JOIN trust_file ON trust_relation_file.file_id = trust_file.file_id ) tarkFileId FROM wound_orders AS wo LEFT JOIN tracking_personnel AS tp ON wo.task_id = tp.id LEFT JOIN doctor ON doctor.id = wo.doctor_id LEFT JOIN trust_relation_file ON wo.id = trust_relation_file.busi_id AND trust_relation_file.busi_type = 'woundOrders' LEFT JOIN trust_file ON trust_relation_file.file_id = trust_file.file_id
wo.order_number like concat('%',#{orderNumber,jdbcType=VARCHAR},'%')
and wo.is_delete = 0 GROUP BY wo.order_number ORDER BY wo.create_time DESC LIMIT #{start,jdbcType=INTEGER},#{end,jdbcType=INTEGER}
